'use strict'
var vows = require('vows')
var assert = require('assert')

var Soundfont = require('../')

vows.describe('Soundfont.noteToMidi').addBatch({
  'note to midi': function () {
    assert.equal(Soundfont.noteToMidi({ midi: 10}), 10)
    assert.equal(Soundfont.noteToMidi(10), 10)
    assert.equal(Soundfont.noteToMidi('c4'), 60)
  }
}).export(module)
